svg.script:
function geplant( id ) {
 var gezeigt = document.getElementById( id ).getAttribute( "display" );
 if( gezeigt == "none" ) {
  // Wird gerade nicht gezeigt, wird als nächstes eingebaut:
  document.getElementById( id ).setAttribute("display","block");
  document.getElementById( id ).setAttribute("transform","translate(0,0)");
  document.getElementById( id ).setAttribute("class", null);
  document.getElementById( id + "_erkl" ).setAttribute("display","none");
  document.getElementById( id + "_aktion" ).firstChild.data = "versetzt anzeigen";
 } else if( gezeigt == "block" ) {
  document.getElementById( id ).setAttribute("class", "geplant");
  document.getElementById( id ).setAttribute("display","inline");
  switch( id ) {
   case "verlängerung_badhomburg":
    document.getElementById( id ).setAttribute("transform","translate(30,0)");
    break;
   case "verlängerung_europaviertel":
    document.getElementById( id ).setAttribute("transform","translate(0,30)");
    break;
   case "ginnheimer_kurve":
   case "frankfurter_berg":
    document.getElementById( id ).setAttribute("transform","translate(45,0)");
    break;
  }
  document.getElementById( id + "_erkl" ).setAttribute("display","inline");
  document.getElementById( id + "_aktion" ).firstChild.data = "verstecken";
  if( id == "verlängerung_badhomburg" ) {
   document.getElementById( "text_gonzenheim" ).setAttribute("class","normalstation");
  }
 } else if( gezeigt == "inline" ) {
  document.getElementById( id ).setAttribute("display","none");
  document.getElementById( id + "_aktion" ).firstChild.data = "einbauen";
 }
 else { }
}

