function kilometer( domobj ) {
 var gezeigt = document.getElementById( domobj + "_km" ).getAttribute( "visibility" );
 if( gezeigt == "visible" ) {
  document.getElementById( domobj + "_km" ).setAttribute("visibility","hidden");
  document.getElementById( domobj + "_tx" ).setAttribute("font-weight","normal");
 } else {
  document.getElementById( domobj + "_km" ).setAttribute("visibility","visible");
  document.getElementById( domobj + "_tx" ).setAttribute("font-weight","bold");
 }
}

function bbgross( domobj ) {
 document.getElementById( "bb_" + domobj ).setAttribute("transform","translate(65,460) scale(3,3)");
 document.getElementById( "gross_" + domobj ).setAttribute("transform","translate(65,460) scale(3,3)");
 document.getElementById( "gross_" + domobj ).setAttribute("class","bbumrandung");
 document.getElementById( "gross_" + domobj ).setAttribute("onclick","javascript:bbklein('"+domobj+"')");
 document.getElementById( "gleisnummern_" + domobj ).setAttribute("display","inline");
}
function bbklein( domobj ) {
 document.getElementById( "bb_" + domobj ).setAttribute("transform","translate(65,460) scale(1,1)");
 document.getElementById( "gross_" + domobj ).setAttribute("transform","translate(65,460) scale(1,1)");
 document.getElementById( "gross_" + domobj ).setAttribute("class","klicken");
 document.getElementById( "gross_" + domobj ).setAttribute("onclick","javascript:bbgross('"+domobj+"')");
 document.getElementById( "gleisnummern_" + domobj ).setAttribute("display","none");
}
