svg.script:
 /// Zeigt oder versteckt geplante Strecke
 ///
 /// Detektiert, ob an- oder ausgeschaltet werden soll anhand des
 /// Textes im Linkelement mit dem Namen <name>_aktion.
 /// @param name Name der geplanten Strecke.
 function geplant(name) {
   var linkElement = document.getElementById(name + "_aktion");
   var aktion = linkElement.firstChild.data == "anzeigen";
   geplant_onoff(name, aktion);
 }

 /// Zeigt oder versteckt geplante Strecke
 /// @param name Name der geplanten Strecke
 /// @param aktion Wenn \c true, dann wird versteckt.
 function geplant_onoff(name, aktion) {
   var linkElement = document.getElementById(name + "_aktion");
   var allStyledElements = document.getElementsByClassName(name);
   for (var i = 0; i < allStyledElements.length; ++i) {
     var classedElement = allStyledElements[i];
     classedElement.style.display = aktion ? 'inline' : 'none';
   }
   linkElement.firstChild.data = aktion ? 'verstecken' : 'anzeigen';
 }

/// Zeigt die Kilometrierung für eine Strecke an oder versteckt sie wieder.
///
/// Detektiert, ob an- oder ausgeschaltet werden soll anhand des
/// Schriftgewichtes im Linkelement mit dem Namen <classname>_tx.
/// @param classname Name der CSS-Klasse, die alle Elemente der
/// Kilometrierung der gewünschten Strecke haben.
function kilometer(classname) {
  var linkElement = document.getElementById(classname + "_tx");
  var zeigen = linkElement.getAttribute("font-weight") != "bold";
  linkElement.setAttribute("font-weight", zeigen ? "bold" : "normal");
  var allStyledElements = document.getElementsByClassName(classname);
  for (var i = 0; i < allStyledElements.length; ++i) {
    var classedElement = allStyledElements[i];
    classedElement.style.display = zeigen ? 'inline' : 'none';
  }
}

/// Schaltet die Haltestellendetails der übergeordneten Gruppe an oder aus:
/// @param clickobj Objekt, auf das geklickt wird; sollte direkt unter
/// der Haltestellengruppe liegen.
function toggle_details(clickobj) {
  toggle_stopdetails(clickobj.parentNode);
}

/// Schaltet die Haltestellendetails dieser Haltestelle an oder aus
/// @param domobj Objekt, dass die zu dieser Haltestelle gehörende
/// SVG-Gruppe enthält.
function toggle_stopdetails(domobj) {
  var detailobjects = domobj.getElementsByClassName("hstdetails");
  if (detailobjects.length == 0) {
    return;
  }
  var aktion = detailobjects[0].style.display != 'inline';
  // alle ausschalten
  var alldetails = document.getElementsByClassName("hstdetails");
  for (var i = 0; i < alldetails.length; ++i) {
    var detailelement = alldetails[i];
    detailelement.style.display = 'none';
  }
  if (! aktion) {
    return;
  }
  // gewünschte einschalten.
  for (var i = 0; i < detailobjects.length; ++i) {
    var detailelement = detailobjects[i];
    detailelement.style.display = 'inline';
  }
}

/// Hebt ein Signal, sein Name und den Standort hervor.
function highlight(domobj) {
  domobj.addEventListener('mouseout', downlight, true);
  domobj.classList.add("highlightedsignal");
  var uses = domobj.getElementsByTagName("use");
  for (var u = 0; u < uses.length; ++u) {
    var href = uses[u].getAttributeNS('http://www.w3.org/1999/xlink','href');
    if (href == '#signalplatz') {
      uses[u].setAttributeNS('http://www.w3.org/1999/xlink','href','#highlightedsignalplatz');
    }
    if (href.startsWith('#signal_')) {
      var ellipses = domobj.getElementsByTagName("ellipse");
      if (ellipses.length > 0) {
        continue;
      }
      var foothighlight = document.createElementNS("http://www.w3.org/2000/svg","ellipse");
      foothighlight.setAttribute('transform',uses[u].getAttribute("transform"));
      foothighlight.setAttribute('rx','7');
      foothighlight.setAttribute('ry','2');
      domobj.insertBefore(foothighlight,domobj.firstChild);
    }
  }
}

function downlight(event) {
  var domobj = event.currentTarget;
  domobj.removeEventListener('mouseout', downlight);
  domobj.classList.remove("highlightedsignal");
  var uses = domobj.getElementsByTagName("use");
  for (var u = 0; u < uses.length; ++u) {
    if (uses[u].getAttributeNS('http://www.w3.org/1999/xlink','href') == '#highlightedsignalplatz') {
      uses[u].setAttributeNS('http://www.w3.org/1999/xlink','href','#signalplatz');
    }
  }
}

function readquery() {
  var query = location.search;
  if (query == "") {
    return;
  }
  if (query.indexOf("?") == 0) {
    query = query.substring(1);
  }
  var fields = query.split("&").sort();
  for (var i = 0; i < fields.length; ++i) {
    var key = fields[i];
    if (key == "debug") {
      document.firstChild.setAttribute("class","debug");
      continue;
    }
    if (key.startsWith("showdetails=")) {
      var group = document.getElementById(key.substring(key.indexOf("=") + 1));
      if (group === undefined) {
        continue;
      }
      toggle_stopdetails(group);
      continue;
    }
    if (key.startsWith("kilometer=")) {
      var value = key.substring(key.indexOf("=") + 1);
      if (value == "") {
        continue;
      }
      kilometer(value);
      continue;
    }
    if (key.startsWith("geplant=")) {
      var value = key.substring(key.indexOf("=") + 1);
      if (value == "") {
        continue;
      }
      geplant_onoff(value, true);
      continue;
    }
    if (key.startsWith("nogeplant=")) {
      var value = key.substring(key.indexOf("=") + 1);
      if (value == "") {
        continue;
      }
      geplant_onoff(value, false);
      continue;
    }
  }
}
